import React, { createContext, useContext, useState } from 'react';

// Create the context
export const TxtStyleContext = createContext();

// Create a provider component
export function TxtStyleProvider({ children }) {
  const [txtstyle, setTxtStyle] = useState({
    color: 'black',
    backgroundColor: 'white',
  });

  return (
    <TxtStyleContext.Provider value={{ txtstyle, setTxtStyle }}>
      {children}
    </TxtStyleContext.Provider>
  );
}
