import { BrowserRouter, Route, Routes } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import './App.css';
import Home from './components/Home';
import NavBar from './components/NavBar';
import SpartanAbode from '../src/components/SpartanAbode';
import Testimonials from './components/Testimonials';
import Alert from './components/Alert';

function App() {
  const [alert, setAlert] = useState(null);
  const [fadeOut, setFadeOut] = useState(false);
  const DisplayAlert=(message,type)=>{
    setAlert({
      message:message,
      type:type,
    });
    setFadeOut(false);
    setTimeout(() => {
      setFadeOut(true);
      setTimeout(() => {
        setAlert(null);
        setFadeOut(false);
      }, ); // Wait for fadeout animation before removing the alert
    }, 1000); 
  }
  const initialMode = localStorage.getItem('mode') || 'light';
  const [mode, setMode] = useState(initialMode);
  useEffect(() => {
    document.body.style.backgroundColor = mode === 'dark' ? 'black' : 'white';
    // Save the mode to local storage whenever it changes
    localStorage.setItem('mode', mode);
  }, [mode]);

  const toggleMode = () => {
    if(mode==='light'){
      setMode('dark');
      DisplayAlert("Dark Mode is enabled","dark");
    }
    else{
      setMode('light');
      DisplayAlert("Light Mode is enabled","success");
    }
  };
  return (
    <>
    <NavBar name="Vinayak Santhosh" mode={mode} toggleMode={toggleMode}/>
    <Alert alert={alert} fadeOut={fadeOut} />
    <BrowserRouter>
        <Routes>
          <Route path='/' element={<Home />}></Route>
          <Route path='/about' element={<SpartanAbode/>}></Route>
          <Route path='/testimonial' element={<Testimonials mode={mode} toggleMode={toggleMode}/>}></Route>
        </Routes>
    </BrowserRouter>
    </>
  );
}


export default App;
