import { BrowserRouter, Route, Routes } from 'react-router-dom';
import React from 'react';
import './App.css';
import Home from './components/Home';
import NavBar from './components/NavBar';
import SpartanAbode from '../src/components/SpartanAbode';
import Testimonials from './components/Testimonials';
import { useDarkMode } from './context/DarkModeContext.js';

function App() {
  const { isDarkMode } = useDarkMode();
  return (
    <div style={{ backgroundColor: isDarkMode ? 'var(--dark-background)' : 'var(--light-background)', color: isDarkMode ? 'var(--dark-text)' : 'var(--light-text)' }}>
    <NavBar name="Vinayak Santhosh"/>
    <BrowserRouter>
        <Routes>
          <Route path='/' element={<Home/>}></Route>
          <Route path='/about' element={<SpartanAbode/>}></Route>
          <Route path='/testimonial' element={<Testimonials/>}></Route>
        </Routes>
    </BrowserRouter>
    </div>
  );
}

export default App;
