import React, { useState } from 'react'

export default function Testionials(props) {
  const [text, setText]=useState('Enter Text Here');
  const handleUpClick=()=>{
    console.log('UpperCase Conversion done');
    setText('You clicked on up click');
  }
  const handleOnChange=(e)=>{
    console.log('On Change activated');
    setText(e.target.value);
  }
  return (
    <div>
        <div><h1>{props.heading}</h1></div>
        <div className="mb-3">
        <textarea className="form-control" id="box" placeholder='Have some balls and say something!!' value={text} onChange={(e)=>handleOnChange(e)} rows="13"></textarea>
        </div>
        <button className='btn btn-primary' onClick={()=>handleUpClick}>Capitalize</button>
    </div>
  )
}
