import { BrowserRouter, Route, Routes } from 'react-router-dom';
import React, {useState} from 'react';
import './App.css';
import Home from './components/Home';
import NavBar from './components/NavBar';
import SpartanAbode from '../src/components/SpartanAbode';
import Testimonials from './components/Testimonials';
import PropTypes from 'prop-types';
function App() {
  const[bgstyle,setBgStyle]=useState({
    color:'white',
    backgroundColor:'black',
});
const handleBackgroundColor=()=>{
    if(bgstyle.color==='white'){
        setBgStyle({
            color:'black',
            backgroundColor:'white',
        })
    }
    else{
        setBgStyle({
            color:'white',
            backgroundColor:'black',
        })
    }
}
  return (
    <div style={bgstyle}>
    <NavBar name="Vinayak Santhosh"/>
    <BrowserRouter>
        <Routes>
          <Route path='/' element={<Home/>}></Route>
          <Route path='/about' element={<SpartanAbode/>}></Route>
          <Route path='/testimonial' element={<Testimonials/>}></Route>
        </Routes>
    </BrowserRouter>
    <button onClick={handleBackgroundColor}>Enable Dark Mode</button>
    </div>
  );
}

export default App;
