import React, { useState } from 'react'
import PropTypes from 'prop-types';

export default function Alert(props) {
    const [msgtype,setMsgType]=useState('dark');
    if(props.alert.type==='light'){
        setMsgType('light');
    }
    else{
        setMsgType('dark');
    }
  return (
    props.alert&&<div class={`alert alert-${msgtype} alert-dismissible fade show`} role="alert">
        {props.alert.message}<button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
    </div>
  )
}

Alert.defaultProps={
    alerttype:PropTypes.string.isRequired,
}

Alert.propTypes={
    alerttype:'Set Alert here',
}
