import { BrowserRouter, Route, Routes } from 'react-router-dom';
import React, { useState } from 'react';
import './App.css';
import Home from './components/Home';
import NavBar from './components/NavBar';
import SpartanAbode from '../src/components/SpartanAbode';
import Testimonials from './components/Testimonials';

function App() {
  const [mode, setmode] = useState('dark');
  const toggleMode=()=>{
    if(mode==='light'){
      setmode('dark');
      document.body.style.backgroundColor='black';
    }
    else{
      setmode('light');
      document.body.style.backgroundColor='white';
    }
  }
  return (
    <>
    <NavBar name="Vinayak Santhosh" mode={mode} toggleMode={toggleMode}/>
    <BrowserRouter>
        <Routes>
          <Route path='/' element={<Home />}></Route>
          <Route path='/about' element={<SpartanAbode/>}></Route>
          <Route path='/testimonial' element={<Testimonials/>}></Route>
        </Routes>
    </BrowserRouter>
    </>
  );
}


export default App;
