import React, { useState } from 'react'

export default function Testionials(props) {
  const [text, setText]=useState('Have some balls and say something!!');
  const [textnew, setText1]=useState();
  const handleUpClick=()=>{
    console.log('UpperCase Conversion done');
    let newText=text.toUpperCase();
    let words=newText.split(' ');
    setText1(newText);
    setText(newText);
    alert(newText);
  }
  const handleOnChange=(e)=>{
    console.log('On Change activated');
    setText(e.target.value);
  }
  return (
    <>
    <div className='container my-3'>
        <div><h1>{props.heading}</h1></div>
        <div className="mb-3">
        <textarea className="form-control" id="box" value={text} onChange={(e)=>handleOnChange(e)} rows="13"></textarea>
        </div>
    </div>
    <button className='btn' onClick={handleUpClick}>Capitalize</button>
    <div className='container'>
        <div><h1>{props.nextheading}</h1></div>
        <div className="mb-3">
        <textarea className="form-control" id="box" value={textnew} onChange={(e)=>handleOnChange(e)} rows="13"></textarea>
        <h4>{textnew.split(' ').length} words and {textnew.length} characters</h4>
        </div>
    </div>
    </>
  )
}
