import { createContext, useContext, useState } from 'react';

const TxtStyleContext = createContext();

export function useTxtStyle() {
  return useContext(TxtStyleContext);
}

export function TxtStyleProvider({ children }) {
    const savedBackgroundColor = localStorage.getItem('backgroundcolor') || 'white';
    const [txtStyle, setTxtStyle] = useState({
      color: savedBackgroundColor === 'white' ? 'black' : 'white',
      backgroundcolor: savedBackgroundColor,
    });


  return (
    <TxtStyleContext.Provider value={{ txtStyle, setTxtStyle }}>
      {children}
    </TxtStyleContext.Provider>
  );
}
