import React, { useState } from 'react'

export default function Testionials(props) {
  const [text, setText]=useState('Have some balls and say something!!');
  const handleUpClick=()=>{
    console.log('UpperCase Conversion done');
    let newText=text.toUpperCase();
    setText(newText);
    alert(newText);
  }
  const handleLowClick=()=>{
    console.log('LowerCase Conversion done');
    let newText=text.toLowerCase();
    setText(newText);
    alert(newText);
  }
  const handleClearClick=()=>{
    console.log('On Change activated');
    setText('');
  }
  const handleOnChange=(e)=>{
    console.log('On Change activated');
    setText(e.target.value);
  }
  return (
    <>
    <div className='container my-3'>
        <h1>{props.heading}</h1>
        <div className="mb-3">
        <textarea className="form-control" value={text} id="box" onChange={(e)=>handleOnChange(e)} rows="13"></textarea>
        </div>
        <button className='btn mx-2' onClick={handleUpClick}>Capitalize</button>
        <button className='btn mx-2' onClick={handleLowClick}>LowerCase</button>
        <button className='btn mx-2' onClick={handleClearClick}>Clear</button>
    </div>
    <div className='container'>
        <h1>{props.nextheading}</h1>
        <h4 className='text-center'>{text}</h4>
        <h4 className='text-center my-4'>{text.split(' ').length} words, {0.0013*text.split(' ').length} average read minutes and {text.length} characters</h4>
    </div>
    </>
  )
}
