import React from 'react'

function PricingBox(props) {
    const pricingBoxClasses = `pricing-box ${props.bgImage ? 'pricing-box-bg-image' : ''} text-center`;
    const pricingBoxStyle = props.bgImage ? { backgroundImage: `url(${props.bgImage})` } : null;

    return (
      <div className={pricingBoxClasses} style={pricingBoxStyle}>
        <h5 className='my-5'>{props.title}</h5>
        <p className="price">
          <sup>$</sup>
          {props.price}
          <sub>/mo</sub>
        </p>
        <ul className="features-list">
          {props.features.map((feature, index) => (
            <li key={index}>
              <strong>{feature.value}</strong> {feature.label}
            </li>
          ))}
        </ul>
        <button className="btn-primary">Get Started</button>
      </div>
    );
  }

  export default function Pricing(props) {
    const plans = [
      {
        title: 'Free',
        price: '0',
        features: [
          { label: 'Project', value: '1' },
          { label: 'Team Members', value: '5' },
          { label: 'Personal Projects', value: '50' },
          { label: 'Messages', value: '5,000' },
        ],
        bgImage:'https://c8.alamy.com/comp/A64PT5/market-place-of-ancient-greek-city-state-of-sparta-hand-colored-woodcut-A64PT5.jpg',
      },
      {
        title: 'Premium',
        price: '39',
        features: [
          { label: 'Project', value: '5' },
          { label: 'Team Members', value: '20' },
          { label: 'Personal Projects', value: '100' },
          { label: 'Messages', value: '15,000' },
        ],
        bgImage:'https://cdna.artstation.com/p/assets/images/images/015/601/820/medium/mario-selvarajan-spartan.jpg?1548935201',
    },
      {
        title: 'Platinum',
        price: '89',
        features: [
          { label: 'Project', value: '25' },
          { label: 'Team Members', value: '50' },
          { label: 'Personal Projects', value: '500' },
          { label: 'Messages', value: '50,000' },
        ],

      },
    ];

    return (
      <div>
        <h1 className="text-center">Pick the best plan for you</h1>
        <div className="pricing-box-container">
          {plans.map((plan, index) => (
            <PricingBox key={index} title={plan.title} price={plan.price} features={plan.features} bgImage={plan.bgImage} />
          ))}
        </div>
      </div>
    );
  }
